(window.webpackJsonp=window.webpackJsonp||[]).push([[57],{500:function(_,v,e){"use strict";e.r(v);var t=e(45),p=Object(t.a)({},(function(){var _=this,v=_.$createElement,e=_._self._c||v;return e("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[e("h1",{attrs:{id:"买了书也不读-还要不要买"}},[e("a",{staticClass:"header-anchor",attrs:{href:"#买了书也不读-还要不要买"}},[_._v("#")]),_._v(" 买了书也不读，还要不要买？")]),_._v(" "),e("h4",{attrs:{id:"原文链接"}},[e("a",{staticClass:"header-anchor",attrs:{href:"#原文链接"}},[_._v("#")]),_._v(" "),e("a",{attrs:{href:"https://mp.weixin.qq.com/s?__biz=MjM5ODQ2MDIyMA==&mid=2650713060&idx=1&sn=f6cfb8bac38591a6df87b3ebe290166c&chksm=bec065b789b7eca1290b229ad77e1ceca7b3f6de389e8dee5a10ef8b47c2b0a8edf495f9c57a&mpshare=1&scene=1&srcid=0301cCLRYnLwWQBj5dqpl8ej#rd",target:"_blank",rel:"noopener noreferrer"}},[_._v("原文链接"),e("OutboundLink")],1)]),_._v(" "),e("p",[_._v("这几天看书的时候，顺便整理了一下书架，原意是把没用的、不准备再读的书收起来，留下准备读和重复阅读的书籍。整理的结果让我大吃一惊，除了一些老旧的技术图书，大部分书都是我准备要读或者曾经准备要读的。这两年纸书堆积如山，其中一半是自己买的，另一半是出版社或其他朋友送的，虽然时时在读，但竟然只读了一小部分。整理的时候，大部分图书都是簇新的。有些书塑封还在，拆开翻阅，新书散发着油墨的书香，我一般翻一边发愣，怎么会有这么多书没看呢？")]),_._v(" "),e("p",[_._v("买书如山倒，读书如抽丝，看来这句话说的一点错也没有。几十年来，房价窜升到了九霄云外，纸书的价格却自巍然不动，只要工作几年，买书就成了负担很轻的消费。有时碰到一本好书，还可能给你带来不可估量的收益。所以，看到一本好书 —— 至少那时候觉得好 —— 我基本上都会冒着不读的风险买下来。过一阵子从书架上拿下来吹去浮尘翻一翻说，咦，果然没读，然后又放了上去。")]),_._v(" "),e("p",[_._v("为什么明知道买书不读还要买呢？我把这个问题扔到了朋友圈。收到了朋友圈的朋友们各式各样的回复：")]),_._v(" "),e("blockquote",[e("p",[_._v("买了再说，万一要是读了呢？")]),_._v(" "),e("p",[_._v("那些年看过的书皮")]),_._v(" "),e("p",[_._v("享受买书的过程")]),_._v(" "),e("p",[_._v("要的是拥有的感觉")]),_._v(" "),e("p",[_._v("为了满足万一想读就能读的控制欲")]),_._v(" "),e("p",[_._v("哦……我认为买书是一种信仰")]),_._v(" "),e("p",[_._v("我每次学新东西，就会买本书放书柜上，马上就有种学会了的感觉，特别棒")])]),_._v(" "),e("p",[_._v("你们说的都对！")]),_._v(" "),e("p",[_._v("从理性角度我很清楚，书架上的图书，在相当长的一段时间内是读不完的，甚至，有些书可能永远都不会读了，一想到这一点我就非常伤感。但见到「好书」，还是会毫不犹豫的买下，无论是电子还是纸质。因为买书可以让人产生一种无与伦比的安全感。")]),_._v(" "),e("p",[_._v("社会越来越复杂，节奏越来越快，选择越来越多，信息的浪潮汹涌澎湃。人们想的太多，做的又太少，在知识的大潮中慌不择路，这时候很多人（包括我）突然发现了一种抗拒焦虑、获取知识的捷径：收藏知识。")]),_._v(" "),e("p",[_._v("从网上下单，付费，收到快递，拆开包装，购书的喜悦油然而生，然后我们喜滋滋的把这本书放在理自己最近的地方，然后等待下一本新书取代它的位置。")]),_._v(" "),e("p",[_._v("Evernote 的剪藏，各种「稍后读」的阅读工具，各类电子书平台，对于很多人来说，都是在收藏知识，因为「反正以后会读的」。至于以后读不读，那是未来的事情，未来嘛，很难讲的。")]),_._v(" "),e("p",[_._v("当然了，收藏知识对我来说，也不是一无是处，毕竟我在一本一本的读着。就像我之前写的，先把一件事情做成，然后再去做另一件事。书读不完不用着急，先读完一本试试。走得慢不要紧，只要你在一直往前走。还没红？继续努力，万一你是大器晚红呢。这么一想，感觉就会好很多。")]),_._v(" "),e("p",[_._v("另外，生活中我们总会遇到某个瞬间，突然想急迫的了解某个领域的知识，这时候如果可以随时抽出书架上一本书翻阅并帮助你答疑去惑，那种解燃眉之急的感觉，就像漆黑的旷野突然被一道闪电照亮，就像猴子终于吃到了香蕉，就像你找到了灌木丛中丢失的手机赶紧回了一条微信那么舒爽。")]),_._v(" "),e("p",[_._v("买书、读书还不够，我们得能找到好书，知道如何去阅读一本书。我在「失业」那篇文章里谈到了好书的概念（因为那篇的重点是失业，这段话基本上被忽略了）。")]),_._v(" "),e("blockquote",[e("p",[_._v("我最近在文章中多次提到读好书的概念，实在是因为我对读书这件事领悟的太晚。小时候杂七杂八读了不少烂书，上大学到毕业工作期间，因为生存问题又读了大量的技术书，真正读到并能够理解的好书屈指可数。慢慢读的书越多，越发现要读到读懂那些经典和名著，是需要运气的。")]),_._v(" "),e("p",[_._v("你得生长在一个不错的知识分子家庭，有好的教育，成长的不同阶段有人告诉你该去读什么书，怎么读书，然后你才能具备了一种阅读能力，帮助你去读很多别人没办法读进去的书。这个过程对我来说实在是太漫长了，因为大部分好书读起来都不是那么让人愉悦的，你需要踮起脚尖，硬着头皮读下去，就像新手进入一个新领域一样，没什么办法，只能坚持往前走。")]),_._v(" "),e("p",[_._v("做到这一点是如此困难，以至于很多人读一些好书都是「从阅读到放弃」，毕竟看一些爆米花文学或影视剧来的更轻松一些。")])]),_._v(" "),e("p",[_._v("除了名师指点，自己如何找到并阅读一本好书呢？今天给大家推荐一本名著 ——《如何阅读一本书》。这本书首次出版于 1940 年，1972 年大幅增补改写后重新出版，延续至今。梁文道老师在他主讲的《我读》系列图书中也推荐过。书中对阅读进行了层次划分，同一本书，因为性质和场景不同，你可以简读，速读，精读，甚至可以用业余学者的方式阅读。")]),_._v(" "),e("p",[_._v("有的人遇到看不懂的图书和文章就会骂作者写的烂，遇到浅显解惑的内容又会认为作者肤浅，却从来不考虑自己的阅读方式和理解问题。有的人希望快速阅读一本书，有的人希望找到好书，有的人则希望能够通读并拆解一本书……这些问题，都可以在本书中找到答案。没找到也别回头来怪我，因为我找着了。")]),_._v(" "),e("p",[_._v("读书和写作还有一个特别大的好处，就是抑制人们心中的恶，我在某问答社区上看到了很多人性的恶，我觉得他们只是在收藏知识，并没有阅读知识。当然，这是另一个话题了，也许会单起一篇去写。")]),_._v(" "),e("p",[_._v("好了，现在听我的，拿起一本书，心无旁骛的把它读完。然后拿起第二本书，再读……你会发现，读书还真是一挺难的事儿。")]),_._v(" "),e("h3",{attrs:{id:"思想假的总结"}},[e("a",{staticClass:"header-anchor",attrs:{href:"#思想假的总结"}},[_._v("#")]),_._v(" 思想假的总结")]),_._v(" "),e("p",[_._v("读书养成 4 步骤：")]),_._v(" "),e("p",[_._v("1）回想")]),_._v(" "),e("p",[_._v("读完一章回想 3-5 分钟")]),_._v(" "),e("p",[_._v("2）写旁批")]),_._v(" "),e("p",[_._v("有启发的内容在旁边写字")]),_._v(" "),e("p",[_._v("3）读后感")]),_._v(" "),e("p",[_._v("整本看完录 5-10 分钟读后感，可以转成文字整理出来。")]),_._v(" "),e("p",[_._v("4）看书评")]),_._v(" "),e("p",[_._v("豆瓣多看看，对比下别人的观点。")]),_._v(" "),e("p",[_._v("就四个步骤，简简单单，没有什么深奥的原理，只需要多训练，老老实实读几本书，训练几个月，习惯就养成了。")])])}),[],!1,null,null,null);v.default=p.exports}}]);